/**
 * Add metadata to the `<head>`.
 *
 * @param {Readonly<Options> | null | undefined} [options]
 *   Configuration (optional).
 * @returns
 *   Transform.
 */
export default function meta(options?: Readonly<Options> | null | undefined): (tree: Root, file: VFile) => undefined;
export type Element = import('hast').Element;
export type Parents = import('hast').Parents;
export type Root = import('hast').Root;
export type VFile = import('vfile').VFile;
export type Image = import('../index.js').Image;
export type Options = import('../index.js').Options;
/**
 * Info passed around.
 */
export type State = Options & StateFields;
/**
 * Extra fields.
 */
export type StateFields = {
    /**
     *   Whether no element was added yet.
     */
    first: boolean;
};
